import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchLead } from "../../actions";

class LeadShow extends Component {
    componentDidMount() {
        this.props.fetchLead(this.props.match.params.id);
    }

    renderLogs() {
        if (this.props.logs.length > 0) {
            logs.map(log => <li>{log}</li>);
        }
    }

    render() {
        if (!this.props.lead) {
            return <div>Loading...</div>;
        }
        const {
            title,
            company,
            first_name,
            last_name,
            profile_image_url,
            level,
            logs,
            location
        } = this.props.lead;
        return (
            <div className="ui segment">
                <div className="ui two column very relaxed grid">
                    <div className="column">
                        <img
                            className="ui small circular image centered"
                            src={profile_image_url}
                        />
                        <h1>
                            {first_name} {last_name}
                        </h1>
                        <h2>{title}</h2>
                        <h3>{company}</h3>
                        <p>
                            <i class="map marker alternate icon">{location}</i>
                        </p>

                        <ul>{this.renderLogs()}</ul>
                        <p />
                    </div>
                    <div className="column">
                        <ul />
                        <p />
                        <p />
                        <p />
                    </div>
                </div>
                <div className="ui vertical divider">and</div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return { lead: state.leads[ownProps.match.params.id] };
};

export default connect(
    mapStateToProps,
    { fetchLead }
)(LeadShow);
